import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Listview.css';

const NewsRow = (props) => {
    const title = props.row.title;
    const date = props.row.date;
    const link = props.row.link;

    const percentage = "100%";
    document.documentElement.style.setProperty('--width', percentage);

    return (
        <li>
            {/* percentage 막대 그래프로 나타내기 (1) */}
            <div class="progress-bar">           
                <div class="progress"> {percentage} </div>
            </div>
            {/* <div className="link">
                {title}
                {link}
                {date}
            </div> */}
        </li>
    );
};

function ListView() {
  const [datas, setDatas] = useState(null);
  const [percentages, setPercent] = useState(null);

  useEffect(() => {
    axios.get('/get-data')
    .then(({data}) => {
        setDatas(data);
    })
    .catch(e => {
        console.error(e.stack);
    });

    axios.get('/percentage')
    .then(({data}) => {
        set(data);
    })
    .catch(e => {
        console.error(e.stack);
    });

  }, []);
  return (
    <ul className='listView'>
    {
        datas && datas.map((v, inx) => {
            const json_v = JSON.parse(v)
            return <NewsRow key={inx} row={json_v} />
        })
    }
    </ul>
  );
}

export default ListView;
