import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Listview.css';

const NewsRow = (props) => {
    const title = props.results.title;
    const date = props.results.date;
    const link = props.results.link;

    const publisher = props.percentages.publisher;
    const fact = props.percentages.fact;
    const conv = props.percentages.conv;
    const fake = props.percentages.fake;

    const fact_p = fact+"%";
    document.documentElement.style.setProperty('--width', fact_p);

    return (
        <li>
            {/* percentage 막대 그래프로 나타내기 (1) */}
            {/* <div class="progress-bar">           
                <div class="progress"> {percentage} </div>
            </div> */}
            <div className="link">
                {fact}
            </div>
        </li>
    );
};

function ListView() {
  const [datas, setDatas] = useState(null);
  const [percentages, setPercent] = useState(null);

  useEffect(() => {
    axios.get('/get-data')
    .then(({data}) => {
        setDatas(data);
    })
    .catch(e => {
        console.error(e.stack);
    });

    axios.get('/percentage')
    .then(({data}) => {
        setPercent(data);
    })
    .catch(e => {
        console.error(e.stack);
    });

  }, []);
  return (
    <ul className='listView'>
    {
        datas && datas.map((v, inx) => {
            percentages && percentages.map((p, ind) => {
                const json_v = JSON.parse(v)
                const json_p = JSON.parse(p)
                return <NewsRow key={inx} results={json_v} percentages={json_p}/>
            })

        })
    }
    </ul>
  );
}

export default ListView;
