import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const Detail = (props) => {
    const row = props.row;
    const title = row.title;   

    return (
      <div className="Detail">
        <div>{title}</div>
      </div>
    )
}

const Chart = (props) => {
  const data_num = props.data_num;
