import React, { useEffect, useState } from 'react';
import axios from 'axios';

const NewsRow = (props) => {
    const date = props.date;
    const title = props.title;
    const link = props.link;

    return (
        <li>
            <div>{date}</div>
        </li>
    );
};

function ListView() {
  const [datas, setDatas] = useState(null);
  const fetchDatas = async () => {  // axios.get으로 아이템 받아오기
    const { data } = await axios.get("http://127.0.0.1:5000/get-data");
    setDatas(data); // 받아온 데이터 datas에 넣기
  };

  useEffect(() => {
    fetchDatas();   // 마운트시 실행되도록 useEffect 사용
  }, []);
  return (
    <ul className='listView'>
    {
        datas &&
        datas.map(data => {
            return <NewsRow date={data["date"]} title={data.title} link={data.link} />
        })
    }
    </ul>
  );
}

export default ListView;
