            </div>
        </li>
    );
};

function ListView() {
  const [datas, setDatas] = useState(null);
  useEffect(() => {
    let apiUrl = 'http://127.0.0.1:5000/get-data';
    axios.get(apiUrl)
    .then(({data}) => {
        setDatas(data);
    })
    .catch(e => {
        console.error(e.stack);
    });
  }, []);
  return (
    <ul className='listView'>
    {

        datas.map(data => {
            return <NewsRow date={data} />
        })
    }
    </ul>
  );
}

export default ListView;
