import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Listview.css';

const NewsRow = (props) => {
    // const title = props.results.title;
    // const date = props.results.date;
    // const link = props.results.link;

    const publisher = props.percentages.publisher;
    const fact = props.percentages.fact;
    const conv = props.percentages.conv;
    const fake = props.percentages.fake;

    const fact_p = fact;
    let class_name = ""
    if (publisher=="JTBC") {
        document.documentElement.style.setProperty('--width-jtbc', fact_p);
        class_name="progress-jtbc"
    } else if (publisher=="MBC") {
        document.documentElement.style.setProperty('--width-mbc', fact_p);
        class_name="progress-mbc"
    } else if (publisher=="SBS") {
        document.documentElement.style.setProperty('--width-sbs', fact_p);
        class_name="progress-sbs"
    } else {
        document.documentElement.style.setProperty('--width-kbs', fact_p);
        class_name="progress-kbs"
    }
    $(".progress-bar .progress-mbc").animate({width: fact_p + '%'}, 1000)
    // let t = 0
    // bar.style.width = 0
    // const barAnimation = setInterval(() => {
    // bar.style.width =  t + '%'
    // t++ >= totalMinwon && clearInterval(barAnimation)
    // }, 10)


    return (
        <li>
            {/* percentage 막대 그래프로 나타내기 (1) */}
            <div>{publisher}</div>
            <div class="progress-bar">    
                <div class={class_name}> {fact_p} </div>
            </div>
            {/* <div className="link">
                {fact}
            </div> */}
        </li>
    );
};

function ListView() {
  const [datas, setDatas] = useState(null);
  const [percentages, setPercent] = useState(null);

  useEffect(() => {
    // axios.get('/get-data')
    // .then(({data}) => {
    //     setDatas(data);
    // })
    // .catch(e => {
    //     console.error(e.stack);
    // });

    axios.get('/percentage')
    .then(({data}) => {
        setPercent(data);
    })
    .catch(e => {
        console.error(e.stack);
    });

  }, []);
  return (
    <ul className='listView'>
    {
        percentages && percentages.map((v, inx) => {
            const json_p = JSON.parse(v)
            return <NewsRow key={inx} percentages={json_p}/>
        })
    }
    </ul>
  );
}

export default ListView;
